import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext';
import { FiAward, FiBarChart2, FiZap, FiPlusCircle } from 'react-icons/fi';

const DashboardPage = () => {
  const { user, login } = useContext(AuthContext);
  const [quizHistory, setQuizHistory] = useState(user?.quizHistory || []);

  useEffect(() => {
    // Function to fetch the latest user data when the page loads
    const fetchUserData = async () => {
      if (!user?.token) return;
      try {
        const config = {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        };
        const { data } = await axios.get(`${process.env.REACT_APP_API_URL}/api/auth/me`, config);
        // Update both the local state for immediate display
        setQuizHistory(data.quizHistory);
        // and update the global user context
        login({ ...data, token: user.token });
      } catch (error) {
        console.error("Failed to fetch latest user data", error);
      }
    };

    fetchUserData();
  }, []); // The empty array ensures this runs once when the component mounts

  if (!user) {
    return <div className="text-center mt-10">Loading...</div>;
  }

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Welcome back, {user.name}!</h1>
        <Link
          to="/create-quiz"
          className="flex items-center justify-center px-6 py-3 bg-green-500 text-white font-bold rounded-lg shadow-md hover:bg-green-600 transition-colors"
        >
          <FiPlusCircle className="mr-2" />
          New Quiz
        </Link>
      </div>

      <div className="grid md:grid-cols-3 gap-6 mb-8">
        <div className="p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md flex items-center">
          <FiZap className="text-yellow-500 text-4xl mr-4" />
          <div>
            <p className="text-gray-500 dark:text-gray-400">Experience</p>
            <p className="text-2xl font-bold">{user.xp || 0} XP</p>
          </div>
        </div>
        <div className="p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md flex items-center">
          <FiBarChart2 className="text-green-500 text-4xl mr-4" />
          <div>
            <p className="text-gray-500 dark:text-gray-400">Level</p>
            <p className="text-2xl font-bold">{user.level || 1}</p>
          </div>
        </div>
        <div className="p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md flex items-center">
          <FiAward className="text-blue-500 text-4xl mr-4" />
          <div>
            <p className="text-gray-500 dark:text-gray-400">Badges</p>
            <p className="text-2xl font-bold">{user.badges?.length || 0}</p>
          </div>
        </div>
      </div>

      <div>
        <h2 className="text-2xl font-bold mb-4">Recent Quizzes</h2>
        <div className="p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md">
          {quizHistory.length > 0 ? (
            <ul className="space-y-4">
              {quizHistory.slice().reverse().slice(0, 5).map((attempt) => (
                <li key={attempt._id} className="flex justify-between items-center p-3 bg-gray-50 dark:bg-gray-700 rounded-md">
                  <div>
                    <p className="font-semibold">Quiz on Topic</p> {/* Placeholder */}
                    <p className="text-sm text-gray-500">{new Date(attempt.date).toLocaleDateString()}</p>
                  </div>
                  <p className="font-bold text-lg">{attempt.score}/{attempt.totalQuestions}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-center text-gray-500">You have not completed any quizzes yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
